import pygame

def check_collision(snake_list, WIDTH, HEIGHT):
    head = snake_list[-1]

    if head[0] >= WIDTH or head[0] < 0 or head[1] >= HEIGHT or head[1] < 0:
        return True

    for segment in snake_list[:-1]:
        if segment == head:
            return True

    return False