import pygame

def move_snake(snake_list, direction, snake_block_size):
    x1 = snake_list[-1][0]
    y1 = snake_list[-1][1]

    if direction == 'LEFT':
        x1 -= snake_block_size
    elif direction == 'RIGHT':
        x1 += snake_block_size
    elif direction == 'UP':
        y1 -= snake_block_size
    elif direction == 'DOWN':
        y1 += snake_block_size

    new_head = [x1, y1]
    snake_list.append(new_head)

    return snake_list

def check_collision(snake_list, WIDTH, HEIGHT):
    head = snake_list[-1]

    if head[0] >= WIDTH or head[0] < 0 or head[1] >= HEIGHT or head[1] < 0:
        return True

    for segment in snake_list[:-1]:
        if segment == head:
            return True

    return False

def grow_snake(snake_list, food_x, food_y, snake_block_size):
    if snake_list[-1][0] == food_x and snake_list[-1][1] == food_y:
        new_head = [snake_list[-1][0], snake_list[-1][1]]
        snake_list.append(new_head)

        return True

    return False